import React, { useEffect, useState, useRef } from "react";
import { GoogleMap, LoadScript } from "@react-google-maps/api";
import Header from "./components/Header";
import Training from "./components/Training"; // Importation du composant Training
import "./App.css"; // Assurez-vous d'importer le fichier CSS

// Clé API Google Maps
const API_KEY = "AIzaSyAf7UIljn6JDxuDYjCJm3o4NR4DzrdK4tA";

// Configuration des options pour la carte Google Maps
const mapContainerStyle = {
  height: "400px",
  width: "100%",
};

// Configuration des options pour la vue Street View
const streetViewContainerStyle = {
  height: "400px",
  width: "100%",
};

// Coordonnées pour le centre de la vue
const center = {
  lat: 48.858844,
  lng: 2.294351,
};

const App = () => {
  const [map, setMap] = useState(null);
  const [activeTab, setActiveTab] = useState("guessr_help"); // État pour les onglets
  const streetViewRef = useRef(null);

  useEffect(() => {
    if (map && streetViewRef.current) {
      // Assurez-vous que l'objet google est défini avant d'utiliser
      if (window.google && window.google.maps) {
        const panorama = new window.google.maps.StreetViewPanorama(
          streetViewRef.current,
          {
            position: center,
            pov: { heading: 34, pitch: 10 },
          }
        );

        map.setStreetView(panorama);
      }
    }
  }, [map]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <LoadScript googleMapsApiKey={API_KEY} libraries={["places"]}>
      <div>
        <Header
          activeTab={activeTab}
          onTabChange={handleTabChange}
          className="header-custom" // Passez la classe CSS ici
        />
        {activeTab === "training" && (
          <div>
            <h1>Training</h1>
            <input
              type="text"
              placeholder="Search..."
              style={{ padding: "10px", width: "300px", marginTop: "10px" }}
            />
            <Training />
          </div>
        )}
        {activeTab === "guessr_help" && (
          <div>
            <h1>Guessr Help</h1>
            {/* Ajoutez ici le contenu pour l'onglet Guessr Help */}
          </div>
        )}
        <div style={mapContainerStyle}>
          <GoogleMap
            onLoad={(map) => setMap(map)}
            mapContainerStyle={mapContainerStyle}
            center={center}
            zoom={2}
          >
            {/* Vous pouvez ajouter des marqueurs ou autres éléments sur la carte ici */}
          </GoogleMap>
        </div>
        <div style={streetViewContainerStyle} ref={streetViewRef}>
          {/* La vue Street View sera rendue ici */}
        </div>
      </div>
    </LoadScript>
  );
};

export default App;
