import React, { useEffect, useState } from "react";
import {
  GoogleMap,
  LoadScript,
  StreetViewPanorama,
} from "@react-google-maps/api";

// Clé API Google Maps
const API_KEY = "AIzaSyAf7UIljn6JDxuDYjCJm3o4NR4DzrdK4tA";

// Configuration des options pour la carte Google Maps
const mapContainerStyle = {
  height: "400px",
  width: "100%",
};

// Configuration des options pour la vue Street View
const streetViewContainerStyle = {
  height: "400px",
  width: "100%",
};

// Coordonnées pour le centre de la vue
const center = {
  lat: 48.858844,
  lng: 2.294351,
};

const App = () => {
  const [map, setMap] = useState(null);
  const [streetView, setStreetView] = useState(null);

  useEffect(() => {
    if (map && streetView) {
      // Configurer la vue Street View pour suivre la vue de la carte
      const panorama = new google.maps.StreetViewPanorama(streetView, {
        position: center,
        pov: { heading: 34, pitch: 10 },
      });

      map.setStreetView(panorama);
    }
  }, [map, streetView]);

  return (
    <LoadScript googleMapsApiKey={API_KEY}>
      <div>
        <div style={mapContainerStyle}>
          <GoogleMap
            onLoad={(map) => setMap(map)}
            mapContainerStyle={mapContainerStyle}
            center={center}
            zoom={14}
          >
            {/* Vous pouvez ajouter des marqueurs ou autres éléments sur la carte ici */}
          </GoogleMap>
        </div>
        <div style={streetViewContainerStyle} ref={setStreetView}>
          {/* La vue Street View sera rendue ici */}
        </div>
      </div>
    </LoadScript>
  );
};

export default App;
