import React, { useState, useCallback } from 'react';
import { GoogleMap, LoadScript, Marker, StreetViewPanorama } from '@react-google-maps/api';
import './App.css';

const GOOGLE_MAPS_API_KEY = 'AIzaSyAf7UIljn6JDxuDYjCJm3o4NR4DzrdK4tA';

const containerStyle = {
  width: '100%',
  height: '100%',
};

const App = () => {
  const [map, setMap] = useState(null);
  const [panorama, setPanorama] = useState(null);

  const onLoadMap = useCallback((mapInstance) => {
    setMap(mapInstance);
  }, []);

  const onLoadPanorama = useCallback((panoramaInstance) => {
    setPanorama(panoramaInstance);
  }, []);

  const handleMapClick = (event) => {
    const clickedLocation = event.latLng;
    const streetViewService = new window.google.maps.StreetViewService();

    // Check if panorama instance is available
    if (panorama) {
      streetViewService.getPanorama(
        { location: clickedLocation, radius: 50 },
        (data, status) => {
          if (status === window.google.maps.StreetViewStatus.OK) {
            panorama.setPosition(clickedLocation);
          } else {
            console.log('Aucune image Street View disponible pour cet emplacement.');
          }
        }
      );
    } else {
      console.log('StreetViewPanorama n\'est pas encore chargé.');
    }
  };

  return (
    <div className="App">
      <div className="header">
        <span className="title">geoguessr aim_map</span>
        <button
          id="toggle-button"
          onClick={() => {
            const container = document.getElementById('container');
            const button = document.getElementById('toggle-button');
            container.classList.toggle('horizontal');
            button.textContent = container.classList.contains('horizontal') ? 'Afficher dessus/dessous' : 'Afficher côte à côte';
          }}
        >
          Afficher côte à côte
        </button>
      </div>
      <div className="help">
        <p>
          Drag & drop le marker sur la vue google maps pour actualiser la vue
          streetview.
        </p>
      </div>
      <div className="container" id="container">
        <div style={{ flex: 1, height: '100%' }}>
          <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={{ lat: 43.3441, lng: 3.2151 }}
              zoom={4}
              onClick={handleMapClick}
              onLoad={onLoadMap}
            >
              {/* Optionally, add a marker or other map elements */}
              <Marker position={{ lat: 43.3441, lng: 3.2151 }} />
            </GoogleMap>
          </LoadScript>
        </div>
        <div style={{ flex: 1, height: '100%' }}>
          <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
            <StreetViewPanorama
              position={{ lat: 43.3441, lng: 3.2151 }}
              pov={{ heading: 165, pitch: 0 }}
              zoom={1}
              onLoad={onLoadPanorama}
            />
          </LoadScript>
        </div>
      </div>
      <footer>deez nuts</footer>
    </div>
  );
};

export default App;
