import React, { useState, useEffect } from "react";
import countries from "../data/countries.json"; // Assurez-vous que le chemin est correct

const GuessrHelp = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredCountries, setFilteredCountries] = useState(countries);

  useEffect(() => {
    // Filtrer et trier les pays
    const sortedFilteredCountries = countries
      .filter((country) =>
        country.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
      .sort((a, b) => a.name.localeCompare(b.name)); // Tri alphabétique

    setFilteredCountries(sortedFilteredCountries);
  }, [searchTerm]);

  return (
    <div>
      <h1>Guessr Help</h1>
      <input
        type="text"
        placeholder="Search countries..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {filteredCountries.map((country) => (
          <li key={country.code}>{country.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default GuessrHelp;
