import React, { useEffect, useRef } from "react";

// Clé API Google Maps
const apiKey = "AIzaSyAf7UIljn6JDxuDYjCJm3o4NR4DzrdK4tA";

// Conteneur pour la vue Street View
const containerStyle = {
  width: "100%",
  height: "400px",
};

// Position initiale pour Street View
const center = {
  lat: 40.73061, // Latitude de New York par défaut
  lng: -73.935242, // Longitude de New York par défaut
};

const StreetViewComponent = () => {
  const streetViewRef = useRef(null);

  useEffect(() => {
    if (streetViewRef.current) {
      const streetView = new window.google.maps.StreetViewPanorama(
        streetViewRef.current,
        {
          position: center,
          pov: {
            heading: 34,
            pitch: 10,
          },
          zoomControl: true,
          addressControl: true,
        }
      );
    }
  }, []);

  return (
    <div>
      <div ref={streetViewRef} style={containerStyle} />
    </div>
  );
};

export default StreetViewComponent;
