import React, { useState } from 'react';
import Header from './components/Header';
import Training from './components/Training';

const App = () => {
  const [activeTab, setActiveTab] = useState('guessr_help');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div>
      <Header activeTab={activeTab} onTabChange={handleTabChange} />
      {activeTab === 'training' && (
        <div>
          <h1>Training</h1>
          <input
            type="text"
            placeholder="Search..."
            style={{ padding: '10px', width: '300px', marginTop: '10px' }}
          />
          <Training />
        </div>
      )}
      {activeTab === 'guessr_help' && (
        <div>
          <h1>Guessr Help</h1>
          {/* Ajoutez ici le contenu pour l'onglet Guessr Help */}
        </div>
      )}
    </div>
  );
};

export default App;
