import React from 'react';
import { GoogleMap, LoadScript } from '@react-google-maps/api';

// Dimensions du conteneur pour la carte
const containerStyle = {
  width: '100%',
  height: '400px'
};

// Position initiale pour Street View
const center = {
  lat: 40.730610, // Latitude de New York par défaut
  lng: -73.935242 // Longitude de New York par défaut
};

// Clé API Google Maps
const apiKey = 'AIzaSyAf7UIljn6JDxuDYjCJm3o4NR4DzrdK4tA';

const StreetViewComponent = () => {
  return (
    <LoadScript
      googleMapsApiKey={apiKey}
    >
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={14}
        options={{
          streetViewControl: true, // Activer le contrôle Street View
        }}
      >
        { /* Street View se gère via le panorama, pas directement dans GoogleMap */ }
        <StreetViewPanorama
          position={center}
          visible={true}
          options={{
            pov: {
              heading: 34,
              pitch: 10,
            },
            zoomControl: true,
            addressControl: true
          }}
        />
      </GoogleMap>
    </LoadScript>
  );
}

export default StreetViewComponent;
